import{Q as a}from"./QImg.af899cac.js";import{Q as o}from"./QPage.8fd825c3.js";import{_ as n,S as r,T as s,U as l,X as e,V as i}from"./index.557a026e.js";const d={name:"AcfoPage"},t={class:"row"},c=e("div",{class:"text-h5 text-center text-bold"}," HISTORIA DE LA MORENADA CENTRAL ",-1),u={style:{width:"100%","text-align":"center",background:"black"}},m=e("div",{class:"text-capitalize text-justify"},[e("br"),e("label",null,' Transcurrieron noventa y nueve a\xF1os desde que prodigiosos cultores del folklore decidieron institucionalizar la "Comparsa de los Morenos" con el nombre de "Morenada Uni\xF3n Comercial" un 29 de Noviembre del a\xF1o de 1924, con el pasar de los a\xF1os cambiando el nombre a "Conjunto Folkl\xF3rico Tradicional Morenada Aut\xE9ntica", de \xE9sta forma contribuyeron a que el Carnaval del Anonimato vaya tomando vigencia en el valioso mundo de los misterios de las tradiciones y costumbres, pues en los rigurosos cumplimientos del orden l\xF3gico se organizaron a trav\xE9s de rodeos de los Prestes, todo este conjunto de hechos vernaculares en Honor a la Virgen del Socav\xF3n, rememorando cada a\xF1o las Entradas del Carnaval, donde los sitios ceremoniales y ritualidades toman validez; hombres como Silvestre Quintanilla, Bernab\xE9 Mamani, Miguel Ma\xEDn, Atanasio Quispe, Ram\xF3n Manuel, Miguel Mamani, Juan Apaza, Celedonio Flores, Faustino Quispe y Ferm\xEDn Huanca entre otros, forman parte de ese legajo hist\xF3rico que merecen los fundadores. '),e("br"),e("br"),e("label",null," El contexto semiol\xF3gico interpretado en la narrativa donde la imagen de los negros africanos al mando de un capataz conceden un escenario en la Vi\xF1a de Mar\xEDa, hecho que se refiere al hecho contestatario del periodo colonial generando un mensaje donde los pueblos lucharon contra el sometiendo del colonialismo espa\xF1ol, la levita, la peluca, el fald\xF3n y los pollerines forman parte del profundo significado que representa esta danza, sin lugar a dudas refleja la s\xE1tira a la esclavitud de los negros producida en las Minas del Asiento Minero de San Miguel. "),e("br"),e("br"),e("label",null,' Un hecho por dem\xE1s significativo se desarrolla entre los a\xF1os de 1944 al 1955, donde inquietos danzarines hijos de fundadores, acuerdan tomar parte de los carnavales para tal efecto se nombra pasante a Dn. Atanacio Quispe y Evarista Colque y como gu\xEDas a Miguel Mamani Apaza, Ram\xF3n Esc\xF3bar, Francisco Apaza y Pedro Apaza, conflictos internos determinan un nuevo lapso de presentaci\xF3n ante el desistimiento del Pasante, hecho que no desanim\xF3 a los danzarines, preservando las tradiciones y costumbres, el a\xF1o de 1955 nuevamente se reorganiza la Morenada tomando el nombre de "Conjunto Tradicional Morenada Aut\xE9ntica Zona Central", cuyo nombre recuperaba la identidad de la actividad comercial dedicados al comercio de la coca y asentados en las calles Cochabamba y Presidente Montes de la ciudad de Oruro, al mismo tiempo que se toma la decisi\xF3n de generar una imagen de identidad propia, para lo cual se presentan en el \xDAltimo Convite de ese Carnaval, el riguroso "traje de convite" constituido por un traje negro acompa\xF1ado con una pa\xF1oleta de seda blanca, sombrero de ala color negro, poncho de vicu\xF1a, guantes blancos, barrilito de plata y acompa\xF1ando el instrumento sonoro conocido como la matraca de quirquincho, s\xEDmbolos que hasta ahora se mantienen; de hecho que el azul y amarillo, uniforme del club deportivo "Uni\xF3n Comercial" y ampliamente empleado por los "aliados" forman parte de esta rica simbolog\xEDa. ')],-1);function p(b,g,v,h,f,y){return r(),s(o,{class:"q-pa-md"},{default:l(()=>[e("div",t,[c,e("div",u,[i(a,{src:"logoCentral.png",width:"200px"})]),m])]),_:1})}var C=n(d,[["render",p]]);export{C as default};
